    }

    .x-menu-item-link:hover {
        background-color: #282a36;
    }

    .x-menu-item-link:hover > * {
        color: #8be9fd;
        font-weight: bold !important;
    }

    .x-toolbar-footer {
        background-color: #44475a;
    }

    .x-window-default {
