/* ==UserStyle==
@name           PayPal - Dark n Simple
@namespace      USO Archive
@author         Gibbu
@description    `Changes PayPal to a dark grey or grey to make it easy for the eyes at low lit areas.`
@version        20181024.13.2
@license        NO-REDISTRIBUTION
@preprocessor   uso
@advanced dropdown theme-d-g "Colour Scheme" {
	d "Dark*" <<<EOT :root {
	--bg0: #090909;
	--bg1: #111;
	--bg2: #171717;
	--bg3: #1c1c1c;
	--bg4: #222;

	--header: #eee;
	--subHeader: #ddd;
	--mainColour: #ccc;
	--darkMainColour: #aaa;
} EOT;
g "Grey" <<<EOT :root {
	--bg0: #222;
	--bg1: #272727;
	--bg2: #2c2c2c;
	--bg3: #333;
	--bg4: #373737;

	--header: #eee;
	--subHeader: #ddd;
	--mainColour: #ccc;
	--darkMainColour: #aaa;
} EOT;

}

==/UserStyle== */
