/* ==UserStyle==
@name           Stylus Ultra Dark
@namespace      USO Archive
@author         IgChi
@description    `!!! THANK YOU FOR YOUR DONATION !!!Ultra Dark Theme for Stylus Chrome Extention.`
@version        20210205.17.53
@license        CC-BY-4.0
@preprocessor   uso
==/UserStyle== */
@-moz-document regexp("chrome-extension://.*") {
::-webkit-scrollbar {
	width: 10px
}
#header::-webkit-scrollbar {
	width: 4px
}
#header::-webkit-scrollbar-thumb {
	background: #222
}
::-webkit-scrollbar-thumb {
	background: #272727
}
::-webkit-scrollbar-thumb:hover {
	background: #333
}
div::-webkit-scrollbar-thumb {
	background: #444
}
div::-webkit-scrollbar-thumb:hover {
	background: #555
}

input[type="checkbox"]:not(.slider):hover, .style-name:hover input[type="checkbox"]:checked, .style-name:hover input[type="checkbox"] {
	background: #111;
	border-color: #ccc;
}
#header, #options, #filters label:hover, #filters .filter-selection:hover {
	color: #b9b9b9;
	background: #111;
	border-color: #333
}
#sections {
	height: unset
}
#header {
	border-right: none!important;
	padding: 0;
	padding-left: 5px;
	box-shadow: none
}
.filter-selection {
	left: 0
}
.filter-selection select {
	width: 230px!important
}
#header .filter-selection label, .filter-selection select {
	height: 24px;
}
#header .filter-selection label .checkmate {
	margin-top: 0
}
#manage-text {
	margin-top: 5px
}
#search-wrapper {
	margin-bottom: 0;
	width: 240px
}
#options-wrapper {
	margin-top: 8px
}
.options-column > div[class="option"] {
	margin-bottom: 8px
}
#header summary, #header #filters summary {
	margin: 0;
}
#mozilla-format-buttons {
	margin-top: 5px;
	margin-bottom: 5px
}
#header h1 {
	text-align: center;
	margin: 0;
	padding-top: 8px;
	padding-bottom: 8px;
	font-size: 22px;
	font-weight: 1000
}
#header summary h2 {
	border-bottom: none!important;
	font-size: 13px
}
#filters {
	border-bottom: 1px dashed #333;
	margin-bottom: 8px
}
#sort-wrapper, #style-actions {
	width: 241px;
	margin-bottom: 0px;
	margin-top: 8px;
	margin-left: 13px;
}
#searchMode {
	box-shadow:-3px 0 2px -3px #6d6d6d!important;
	background: #222!important;
	border-radius: 0;
	width: 86px!important
}
.section-editor .section, .section-editor .section:not(:first-child) {
	margin: 10px;
	margin-bottom: 10px;
	padding: 0;
	padding-bottom: 10px;
	border: none;
	border-radius: 5px;
	background: #151515;
	box-shadow: 0 0 10px -5px black;
}
#sections > .section:not(.removed) > label {
	width: calc(100% - 20px);
	background: #151515!important;
	padding: 6px 10px;
	border-radius: 5px;
	box-shadow: 0px 3px 4px -3px #00000082
}
#sort-wrapper .select-arrow, .svg-icon.select-arrow, .filter-selection .select-arrow {
	margin: 0;
	height: 100%;
	width: 14px;
	top: 0;
	right: 4px;
}
#header details, #header details:not([open]), #mozilla-format-container {
	background: #111;
	margin: 0;
	margin-left: 4px;
	margin-right: 5px;
	margin-bottom: 8px;
	padding: 8px;
	padding-left: 8px;
	padding-right: 0;
	border: 1px dashed #333;
}
#mozilla-format-container h2 {
	margin: 0;
	margin-bottom: 8px
}
#mozilla-format-container {
	margin-top: 15px;
	width: 255px
}
#basic-info-enabled {
	justify-content: center
}
#basic-info, #stylus-edit #actions {
	padding-left: 4px
}
#stylus-edit #actions {
	padding-bottom: 8px
