/* ==UserStyle==
@name           Microsoft Login - Dark Mode
@namespace      USO Archive
@author         AlienDrew
@description    `Makes the entire microsoft login dark.`
@version        20210103.11.21
@license        CC0-1.0
@preprocessor   uso
==/UserStyle== */
@-moz-document regexp("^((https?:\\/\\/)?(login|signup|account)\\.)?(microsoft|microsoftonline|live)+\\.com?(.*)$") {
/* inverts */
:root {
  --special-background-material-dark:#202020; /* for compliance in material design - main background */
