sections.code:
:root {
    --regular-text: #e3e3e3;
    --header-text: #ccd6e1;
    --button-background: #222;
    --button-border: #004b60;
}

html, body {
    background-color: black;
    color: var(--regular-text);
    display: block;
    margin-left: auto;
    margin-right: auto;
    width: 85%;
}

/*Some images*/
/*
List:
.Header-logo
.Hero-gopher
img.gopher
.Footer-gopher
*/
img {
    opacity: 0.8;
}

img:Hover {
    opacity: 1;
}

.HeroDownloadButton-image {
    filter: invert(90%);
    opacity: 1;
}

/*Header Menu*/
.HeaderSearch {
	border: 0.12rem solid #444;
	border-radius: 0.1875rem;
}

.HeaderSearch-input {
	background-color: #000;
    color: var(--regular-text);
}

.HeaderSearch-submit {
	background-color: #000;
	transition: background-color 200ms;
}

.HeaderSearch-icon {
	fill: #c6c6c6;
}

a, .exampleHeading .text, .expandAll {
	color: #009ec6;
}

.Header-menuItem, .Header-menuItem a:link, .Header-menuItem a:visited {
	color: var(--header-text) !important;
	text-align: center;
}

.Blog-when, .Footer-supportedBy, .Hero-description {
	color: #a6a6a6;
}

/*Articles*/
