sections.code:
/* ==UserStyle==
@name        Goodreads dark (Jade) theme
@namespace   https://github.com/obscuredetour/
@version     1.8.4
@license     MIT
@author      obscure detour (https://www.jeffreysummers.me)
@homepageURL https://github.com/obscuredetour/goodreads-dark
@updateURL   https://github.com/obscuredetour/goodreads-dark/raw/master/goodreads-dark.user.css
==/UserStyle== */

:root {
    /* color variables - darkest to lightest */
    --jet: hsl(0, 0%, 12%);
    --space: hsl(0, 0%, 13%);
    --raven: hsl(0, 1%, 15%);
    --asher: hsl(0, 1%, 17%);
    --aztec: hsl(0, 1%, 20%);
    --cape-cod: hsl(0, 0%, 23%);
    --corduroy: hsl(0, 1%, 26%);
    --mine: hsl(0, 0%, 28%);
    --mystic: hsl(0, 0%, 38%);
    --grey: hsl(0, 0%, 67%);
    --alabaster: hsl(168, 15%, 83%);
    --zircon: hsl(0, 2%, 91%);
    --concrete: hsl(0, 0%, 95%);

    --limestone: hsl(168, 15%, 17%);
    --elm: hsl(169, 51%, 32%);
    --jade: hsl(165, 42%, 35%);
    --robin: hsl(166, 18%, 38%);
    --neptune: hsl(165, 34%, 60%);
    --conch: hsl(165, 13%, 65%);
    --green-shadow: hsl(165, 25%, 66%);
    --powder-ash: hsl(165, 13%, 75%);
    --mint-ice: hsl(165, 70%, 90%);

    /* backgrounds */
    --bkg-nav: var(--jet);
    --bkg-main: var(--space);
    --bkg-card: var(--raven);
    --bkg-card-foot: var(--asher);
    --bkg-nav-link-hover: var(--corduroy);
    --bkg-button: var(--jade);
    --bkg-button-hover: var(--elm);

    /* text */
    --txt-main: var(--concrete);
    --txt-second: var(--zircon);
    --txt-grey: var(--grey);
    --txt-nav-link: var(--alabaster);
    --txt-link: var(--neptune);
    --txt-link-bright: var(--mint-ice);
    --txt-button: white;

    /* borders */
    --border: var(--mine);
    --border-bright: var(--mystic);
  }

  /* Main Pages & Sections */
  body,
  .gr-homePageBody,
  .mainContent,
  .mainContentFloat,
  #rating_graph,
  .rating_graph,
  .mainContentContainer,
  .dropdown__menu--wantToReadMenu,
  .gr-popover,
  #editFavGenres .recsNotice,
  .newsHeader,
  .aboutRightColumnWide section,
  .aboutRightColumnWide section h2,
  #box #boxContents {
    background-color: var(--bkg-main);
    color: var(--txt-second);
  }

  .mainContentContainer#topLanding {
    padding: 25px 0
  }

  /* Site logo */
  .siteHeader__logo,
  .logo a img,
  #signedOutHome .discoveryBoxDiscovery .discoveryBoxArrow img {
    filter: brightness(4) sepia(55%) hue-rotate(115deg) saturate(34%)
  }
  /* Site logo reset to default - sorry GR */
  .siteHeader__logo {
    background: url("https://s.gr-assets.com/assets/layout/header/goodreads_logo.svg") no-repeat;
    background-position: center;
    background-size: 140px 30px;
  }

  .siteHeader__logo.siteHeader__logo--doodle { filter: brightness(.75) sepia(55%) hue-rotate(115deg) saturate(34%) }

  #siteheader #logo img {
    filter: brightness(2) sepia(55%) hue-rotate(115deg) saturate(54%)
  }

  #siteheader #logo {
    position: relative
  }

  #siteheader #logo:after {
    content: 'dark (jade)';
    position: absolute;
    right: -4rem;
    top: 0;
    font-size: 13px;
    color: var(--limestone);
  }

  .gr-icon--authorBadge {
    filter: brightness(3) sepia(55%) hue-rotate(115deg) saturate(34%)
  }

  /* Personal Nav icons */
  .headerPersonalNav__icon,
  #signedOutHome #masthead #headline img {
    filter: brightness(1) sepia(55%) hue-rotate(115deg) saturate(34%)
  }

  img.circularIcon[src*="nophoto"],
  img[src*="nophoto"],
  .wantToReadButton__checkIcon--read {
    filter: brightness(0.75) sepia(55%) hue-rotate(115deg) saturate(34%)
  }

  /* Social icons */
  .gr-footer__socialLink img,
  .gr-h1 img,
  .responsiveSiteFooterV2__socialLink img,
  .responsiveSiteFooter__socialLink img {
    filter: invert(1)
  }

  .brownBox a[id*='folder_'] img,
  a img[src*='arrow'] {
    filter: invert(0.75)
  }

  .dropdown__trigger--profileMenu .headerPersonalNav__icon {
    filter: unset
  }

  .gr-iconButton img[src*="close"] img {
    filter: brightness(2)
  }

  .gr-shareButton__icon {
    filter: brightness(185%) hue-rotate(-12deg) saturate(34%)
  }

  #signedOutHome .discoveryBoxDiscovery {
    background-image: linear-gradient(var(--aztec), var(--aztec)),
      url(data:image/png;base64,iVBORw0KGgoAAAANSUhEUgAAAAEAAAAoCAYAAAA%2FtpB3AAAAOElEQVQIW2M6uG%2FlfyZGRkYGdIIBCKAsRgYMWXSCAU5gUYzVAAaELA5tCHUQAxjQZIFir16%2FhXAByw8GmnhQCm8AAAAASUVORK5CYII%3D);
    background-blend-mode: saturation;
  }

  #signedOutHome .mainContentContainer {
    background-image: linear-gradient(var(--bkg-main), var(--bkg-main)),
      url(data:image/png;base64,iVBORw0KGgoAAAANSUhEUgAAAAEAAAASCAYAAACaV7S8AAAALElEQVQIW2P4DwRMnz48YWBiAAIIwcjIBCIYgVw4CyLGAGOhSyApBpoBMhQAWY0LCsZmAMgAAAAASUVORK5CYII%3D);
    background-blend-mode: saturation;
  }

  .reviewFormRereading table tr td .readingSessionDatePicker.smallPicker,
  .reviewFormCurrentlyReading table tr td .readingSessionDatePicker.smallPicker,
  .reviewFormRereading table tr td .readingSessionDatePicker.largePicker,
  .reviewFormCurrentlyReading table tr td .readingSessionDatePicker.largePicker {
    position: relative;
    background: unset;
  }

  .reviewFormRereading table tr td .readingSessionDatePicker.smallPicker::after,
  .reviewFormCurrentlyReading table tr td .readingSessionDatePicker.smallPicker::after,
  .reviewFormRereading table tr td .readingSessionDatePicker.largePicker::after,
  .reviewFormCurrentlyReading table tr td .readingSessionDatePicker.largePicker::after {
    border-color: var(--txt-grey) transparent;
    border-style: solid;
    border-width: 4px 4px 0px 4px;
    height: 0px;
    width: 0px;
    content: '';
    position: absolute;
    top: 12px;
    right: 6px;
  }

  /* radio not checked */
  .shelfChooser .shelves li.exclusive {
    background-image: url(data:image/png;base64,);
  }

  /* radio checked */
  .shelfChooser .shelves li.exclusive_chosen {
    background-image: url(data:image/png;base64,);
  }

  /* square not check */
  .shelfChooser .shelves li {
