/* ==UserStyle==
@name           Goodreads dark (jade) theme
@namespace      USO Archive
@author         obscuredetour
@description    `A dark theme for <a href="https://www.goodreads.com">goodreads.com</a>. <b>GitHub (Use links below for easy install & auto updates)</b><a href="https://github.com/obscuredetour/goodreads-dark">GitHub Repository</a><a href="https://github.com/obscuredetour/goodreads-dark/raw/master/goodreads-dark.user.css"><b>Click here to install</b></a><a href="https://github.com/obscuredetour/goodreads-dark/raw/master/goodreads-dark-og.user.css"><b>Click here to install OG version</b></a><b>Screenshots</b><a href="https://raw.githubusercontent.com/obscuredetour/goodreads-dark/master/images/ss-gr-login-zs.png">Screenshot - goodreads.com Login</a><b>OG version</b><a href=https://raw.githubusercontent.com/obscuredetour/goodreads-dark/master/images/ss-gr-login-og.png">Screenshot - goodreads.com Login</a><a href="https://raw.githubusercontent.com/obscuredetour/goodreads-dark/master/images/ss-gr-2018-choice.png">Screenshot - goodreads.com 2018 Choice Awards</a>`
@version        20191116.0.20
@license        NO-REDISTRIBUTION
@preprocessor   uso
==/UserStyle== */
/* ==UserStyle==
@name        Goodreads dark (Jade) theme
@namespace   https://github.com/obscuredetour/
@version     1.8.4
@license     MIT
@author      obscure detour (https://www.jeffreysummers.me)
@homepageURL https://github.com/obscuredetour/goodreads-dark
@updateURL   https://github.com/obscuredetour/goodreads-dark/raw/master/goodreads-dark.user.css
==/UserStyle== */
@-moz-document domain("goodreads.com") {
  :root {
    /* color variables - darkest to lightest */
    --jet: hsl(0, 0%, 12%);
    --space: hsl(0, 0%, 13%);
    --raven: hsl(0, 1%, 15%);
    --asher: hsl(0, 1%, 17%);
    --aztec: hsl(0, 1%, 20%);
    --cape-cod: hsl(0, 0%, 23%);
    --corduroy: hsl(0, 1%, 26%);
    --mine: hsl(0, 0%, 28%);
    --mystic: hsl(0, 0%, 38%);
    --grey: hsl(0, 0%, 67%);
    --alabaster: hsl(168, 15%, 83%);
    --zircon: hsl(0, 2%, 91%);
    --concrete: hsl(0, 0%, 95%);

    --limestone: hsl(168, 15%, 17%);
    --elm: hsl(169, 51%, 32%);
    --jade: hsl(165, 42%, 35%);
    --robin: hsl(166, 18%, 38%);
    --neptune: hsl(165, 34%, 60%);
    --conch: hsl(165, 13%, 65%);
    --green-shadow: hsl(165, 25%, 66%);
    --powder-ash: hsl(165, 13%, 75%);
    --mint-ice: hsl(165, 70%, 90%);

    /* backgrounds */
    --bkg-nav: var(--jet);
    --bkg-main: var(--space);
    --bkg-card: var(--raven);
    --bkg-card-foot: var(--asher);
    --bkg-nav-link-hover: var(--corduroy);
    --bkg-button: var(--jade);
    --bkg-button-hover: var(--elm);

    /* text */
    --txt-main: var(--concrete);
    --txt-second: var(--zircon);
    --txt-grey: var(--grey);
    --txt-nav-link: var(--alabaster);
    --txt-link: var(--neptune);
    --txt-link-bright: var(--mint-ice);
    --txt-button: white;

    /* borders */
    --border: var(--mine);
    --border-bright: var(--mystic);
  }

  /* Main Pages & Sections */
  body,
  .gr-homePageBody,
  .mainContent,
  .mainContentFloat,
  #rating_graph,
  .rating_graph,
  .mainContentContainer,
  .dropdown__menu--wantToReadMenu,
  .gr-popover,
  #editFavGenres .recsNotice,
  .newsHeader,
  .aboutRightColumnWide section,
  .aboutRightColumnWide section h2,
  #box #boxContents {
    background-color: var(--bkg-main);
    color: var(--txt-second);
  }

  .mainContentContainer#topLanding {
    padding: 25px 0
  }

  /* Site logo */
  .siteHeader__logo,
  .logo a img,
  #signedOutHome .discoveryBoxDiscovery .discoveryBoxArrow img {
    filter: brightness(4) sepia(55%) hue-rotate(115deg) saturate(34%)
  }
  /* Site logo reset to default - sorry GR */
  .siteHeader__logo {
    background: url("https://s.gr-assets.com/assets/layout/header/goodreads_logo.svg") no-repeat;
    background-position: center;
    background-size: 140px 30px;
  }

  .siteHeader__logo.siteHeader__logo--doodle { filter: brightness(.75) sepia(55%) hue-rotate(115deg) saturate(34%) }

  #siteheader #logo img {
    filter: brightness(2) sepia(55%) hue-rotate(115deg) saturate(54%)
  }

  #siteheader #logo {
    position: relative
  }

  #siteheader #logo:after {
    content: 'dark (jade)';
    position: absolute;
