  }

  /* card footer width expansion */
  .likeInformation,
  .gr-newsfeedItem__footer .gr-comments,
  .gr-newsfeedItem__footer,
  .gr-commentForm {
    width: calc(100% + 0.25rem);
    margin-left: -0.75rem;
    padding: 0 0.25rem 1rem 0.75rem;
    border: 0;
  }

  .likeInformation,
