  /* list tweaks */
  .tableList td,
  .tableList th,
  .quote,
  .brownBox,
  .newsMainContent.mainContentFloat {
    padding: 1rem 0.5rem;
  }

  .brownBox {
    padding: 0.5rem;
    border-radius: 6px
