  .contentBox,
  .jobsValuesBox,
  .jobsValuesButton,
  .mainContentContainer#topLanding {
    box-shadow: none;
    -webkit-box-shadow: none;
  }

  main {
    padding-top: 1rem
  }

  .distractionless #header {
    margin-bottom: 0
  }

  div.bannerYear[style] {
    background-color: var(--elm) !important
  }

  /* Best of Lists */
  .gcaDivider,
  .gcaDivider__centerpiece {
    height: 0
  }

  /* News & Interviews tweaks */
  .newsMainContent.mainContentFloat,
  #box {
    background: transparent;
  }

  /* Revert serif to sans-serif */
  .newsContainer,
  .comment .reviewText {
    font-family: "Lato", "Helvetica Neue", "Helvetica", -apple-system, BlinkMacSystemFont, Roboto, 'Segoe UI', Oxygen, Ubuntu, Cantarell, 'Open Sans', sans-serif
  }

  #box #boxContents {
    margin-top: 0;
    padding-top: 22px;
  }

  /* Review comments */
  .reviewFooter>div[id*='comments_for'] div:first-child {
    border-top-left-radius: 6px;
    border-top-right-radius: 6px;
  }

  .reviewFooter>div[id*='comments_form_review'].brown_comment {
    border-bottom-right-radius: 6px;
    border-bottom-left-radius: 6px;
  }

  .reviewFooter>div[id*='comments_form_review'].brown_comment>textarea,
  .reviewFooter>div[id*='comments_form_review'].brown_comment #comment_body_usertext,
  .shelfChooser input[type=text] {
    background-color: var(--asher)
  }

  /* Remove top banner */
  .siteHeader__topFullImageContainer { display: none; }
  html.withSiteHeaderTopFullImage .siteHeaderBottomSpacer {
    padding-bottom: 3rem;
  }
  html.withSiteHeaderTopFullImage .primaryNavMenu__menu,
  html.withSiteHeaderTopFullImage .dropdown__menu--profileMenu,
  html.withSiteHeaderTopFullImage .dropdown__menu--siteHeader { top: initial }
}

sections.code:
:root {
		--primary-color: #18191a;
		--secondary-color: #242526;
		--third-color: #3a3b3c;
		--text: teal;
	}

	body {
		background: var(--primary-color);
	}

	.uk-box-shadow, .uk-card-default, .ls-main {
		box-shadow: 0 5px 15px var(--secondary-color);
	}

	.uk-box-shadow:hover, .ls-suggestion {
		box-shadow: 0 5px 15px var(--third-color);
	}

	.ls-suggestion:hover {
		box-shadow: 0 5px 15px var(--text);
	}

	.uk-card-default, .ls-footer, .uk-button-primary:focus, .uk-button-primary:hover, .uk-input:focus, .ls-pre, .uk-accordion-title:hover {
		background: var(--secondary-color);
	}

	.uk-alert-danger, .uk-button-primary, .uk-input {
		background: var(--third-color);
	}

	small, .uk-button-primary, .uk-button-primary:hover, .uk-button-primary:focus, .ls-footer p, .uk-text-primary,.uk-link, a, .uk-h4, h4 {
		color: var(--text)!important;
	}

	.uk-tab>.uk-active>a, pre, hr, .uk-divider-small::after, .uk-heading-line>::after, .uk-heading-line>::before {
		border-color: var(--text);
	}

	.uk-tab::before, .uk-accordion-title {
		border-color: var(--third-color);
	}

