/* ==UserStyle==
@name			GitHub Raw
@author			Domenico Blanco
@namespace		GitHub
@version		1.0.0
@preprocessor	default
==/UserStyle== */

@-moz-document domain("raw.githubusercontent.com"), domain("raw.github.com"){
	html {
		background: black;
		color: teal;
	}
}

